import { useEffect, useState } from "react";


const App = () => {
const [data, setData] = useState(null);


  useEffect(() => {
  fetch ("https://api.themoviedb.org/3/movie/popular?api_key=b6247a3272129a1a56aa5b9fcd55bdc1")
    .then(response => response.json())
    .then(data => setData(data));
},[])

  return( 
    
  <div className="container">
    <h1>hola peliculas</h1>
    <img src="https://image.tmdb.org/t/p/w200/NNxYkU70HPurnNCSiCjYAmacwm.jpg" alt="img"/>
   
      <ul>
      {data?.map((user)=> (
      <li key={user.id}>{user.name}</li>
      ))}
    </ul>
    
    
    </div>);
}


export default App;
